aregiver."},{"name":"nonparent_total_number_of_reviews","type":"number","required":true,"description":"=The number of reviews for this company by employees who self-identified as not a parent or guardian."},{"name":"nonveteran_average_rating","type":"number","required":true,"description":"=The average rating for this company by employees who self identified as non-veterans."},{"name":"nonveteran_total_number_of_reviews","type":"number","required":true,"description":"=The number of reviews for this company by employees who self-identified as non-veterans."},{"name":"veteran_average_rating","type":"number","required":true,"description":"=The average rating for this company by employees who self identified as veterans."},{"name":"veteran_total_number_of_reviews","type":"number","required":true,"description":"=The number of reviews for this company by employees who self-identified as veterans."}]}},"typeVersion":1},{"id":"c8d9e45c-7d41-47bd-b9a9-0fa70de5d154","name":"Define contributions to variance","type":"n8n-nodes-base.set","position":[2560,200],"parameters":{"options":{},"assignments":{"assignments":[{"id":"7360b2c2-1e21-45de-8d1a-e72b8abcb56b","name":"contribution_to_variance.5_star","type":"number","value":"={{ ($json.output['5_star_distribution_percentage'] / 100)  * Math.pow(5 - $json.output.average_rating,2) }}"},{"id":"acdd308a-fa33-4e33-b71b-36b9441bfa06","name":"contribution_to_variance.4_star","type":"number","value":"={{ ($json.output['4_star_distribution_percentage'] / 100)  * Math.pow(4 - $json.output.average_rating,2) }}"},{"id":"376818f3-d429-4abe-8ece-e8e9c5585826","name":"contribution_to_variance.3_star","type":"number","value":"={{ ($json.output['3_star_distribution_percentage'] / 100)  * Math.pow(3 - $json.output.average_rating,2) }}"},{"id":"620d5c37-8b93-4d39-9963-b7ce3a7f431e","name":"contribution_to_variance.2_star","type":"number","value":"={{ ($json.output['2_star_distribution_percentage'] / 100)  * Math.pow(2 - $json.output.average_rating,2) }}"},{"id":"76357980-4f9b-4b14-be68-6498ba25af67","name":"contribution_to_variance.1_star","type":"number","value":"={{ ($json.output['1_star_distribution_percentage'] / 100)  * Math.pow(1 - $json.output.average_rating,2) }}"}]}},"typeVersion":3.4},{"id":"8ea03017-d5d6-46ef-a5f1-dae4372f6256","name":"Set variance and std_dev","type":"n8n-nodes-base.set","position":[2740,200],"parameters":{"options":{},"assignments":{"assignments":[{"id":"3217d418-f1b0-45ff-9f9a-6e6145cc29ca","name":"variance","type":"number","value":"={{ $json.contribution_to_variance.values().sum() }}"},{"id":"acdb9fea-15ec-46ed-bde9-073e93597f17","name":"average_rating","type":"number","value":"={{ $('Extract overall ratings and distribution percentages').item.json.output.average_rating }}"},{"id":"1f3a8a29-4bd4-4b40-8694-c74a0285eadb","name":"total_number_of_reviews","type":"number","value":"={{ $('Extract overall ratings and distribution percentages').item.json.output.total_number_of_reviews }}"},{"id":"1906c796-1964-446b-8b56-d856269da938","name":"std_dev","type":"number","value":"={{ Math.sqrt($json.contribution_to_variance.values().sum()) }}"}]}},"typeVersion":3.4},{"id":"0570d531-8480-4446-8f02-18640b4b891e","name":"Calculate P-Scores","type":"n8n-nodes-base.code","position":[3340,440],"parameters":{"jsCode":"// Approximate CDF for standard normal distribution
function normSDist(z) {
  const t = 1 / (1 + 0.3275911 * Math.abs(z));
  const d = 0.254829592 * t - 0.284496736 * t * t + 1.421413741 * t * t * t - 1.453152027 * t * t * t * t + 1.061405429 * t * t * t * t * t;
  return 0.5 * (1 + Math.sign(z) * d * Math.exp(-z * z / 2));
}

for (const item of $input.all()) {
  if (!item.json.population_analysis.p_scores) {
    item.json.population_analysis.p_scores = {};
  }

  for (const score of Object.keys(item.json.population_analysis.z_scores)) {
    // Check if review count exists and is greater than zero
    if (item.json.population_analysis.review_count[score] > 0) {
      // Apply the p_score formula: 2 * NORM.S.DIST(-ABS(z_score))
      const p_score = 2 * normSDist(-Math.abs(item.json.population_analysis.z_scores[score]));

      // Store the calculated p_score
      item.json.population_analysis.p_scores[score] = p_score;
    } else {
      // Remove z_scores, effect_sizes, and p_scores for groups with no reviews
      delete item.json.population_analysis.z_scores[score];
      delete item.json.population_analysis.effect_sizes[score];
      delete item.json.population_analysis.p_scores[score];
    }
  }
}

