// Purpose: n8n Function node to clean HTML: remove doctype, scripts, styles, head, comments, classes, extra blank lines, and non-whitelisted tags

return items.map(item => {
  const rawHtml = item.json.data;

  // 1) remove doctype, scripts, styles, comments and head section, and strip class attributes
  let cleaned = rawHtml
    .replace(/<!doctype html>/gi, '')
    .replace(/<script[\s\S]*?<\/script>/gi, '')
    .replace(/<style[\s\S]*?<\/style>/gi, '')
    .replace(/<!--[\s\S]*?-->/g, '')
    .replace(/<head[\s\S]*?<\/head>/gi, '')
    .replace(/\sclass="[^"]*"/gi, '');

  // 2) define whitelist of tags to keep
  const allowedTags = [
    'h1','h2','h3','h4','h5','h6',
    'p','ul','ol','li',
    'strong','em','a','blockquote',
    'code','pre'
  ];

  // 3) strip out all tags not in the whitelist, reconstruct allowed tags cleanly
  cleaned = cleaned.replace(
    /<\/?([a-z][a-z0-9]*)\b[^>]*>/gi,
    (match, tagName) => {
      const name = tagName.toLowerCase();
      if (allowedTags.includes(name)) {
        return match.startsWith('</') ? `</${name}>` : `<${name}>`;
      }
      return '';
    }
  );

  // 4) collapse multiple blank or whitespace-only lines into a single newline
  cleaned = cleaned.replace(/(\s*\r?\n\s*){2,}/g, '\n');

  // 5) trim leading/trailing whitespace
  cleaned = cleaned.trim();

  return {
    json: { cleanedHtml: cleaned }
  };
});"},"typeVersion":2},{"id":"f72660d5-8427-4655-acbe-10365273c27b","name":"extract data","type":"@n8n/n8n-nodes-langchain.chainLlm","position":[680,60],"parameters":{"text":"={{ $json.cleanedHtml }}","messages":{"messageValues":[{"message":"=You are an expert in web page scraping. Provide a structured response in JSON format. Only the response, without commentary.

