        <button class="button cancel-button" onclick="closeWindow()">Cancel</button>
      </div>
      <div class="editor-preview-container">
        <textarea id="editor">{{ $json.workflowdata }}</textarea>
        <div class="preview-container">
          <div id="preview"></div>
        </div>
      </div>
    </div>

<script>
  const editor = document.getElementById('editor');
  let vm;

      mermaid.initialize({
        startOnLoad: false,
      });

      let svgCounter = 0;

      // Function to save content
      async function saveContent() {
        try {
          const response = await fetch(window.location.pathname + '?action=save', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              content: editor.value
            })
          });

          if (response.ok) {
            alert('Successfully saved!');
          } else {
            alert('Failed to save content');
          }
        } catch (error) {
          console.error('Error saving content:', error);
          alert('Error saving content');
        }
      }

      // Function to close window
      function closeWindow() {
        window.close();
      }

  window.$docsify = {
    el: '#preview',
    loadSidebar: false,
    loadNavbar: false,
    basePath: '/',
    hideSidebar: true,
        markdown: {
          renderer: {
            code(code, lang) {
              if (lang === "mermaid") {
                const svgName = `mermaid-svg-${svgCounter++}`;
                const MERMAID_CONTAINER_ID = `${svgName}-container`;
                mermaid.render(svgName, code).then(({ svg }) => {
                  const containerElement = document.querySelector(
                    `#${MERMAID_CONTAINER_ID}`
                  );
                  if (containerElement) {
                    containerElement.innerHTML = svg;
                  } else {
                    console.error(`Error: #${MERMAID_CONTAINER_ID} not found`);
                  }
                });
                return `<div class="mermaid" id="${MERMAID_CONTAINER_ID}"></div>`;
              }
              return this.origin.code.apply(this, arguments);
            },
          },
        },
    plugins: [
      function(hook, _vm) {
        vm = _vm;

        hook.beforeEach(function(content) {
          return editor.value;
        });
      }
    ]
  };

let timeout;
function updatePreview() {
  clearTimeout(timeout);
  timeout = setTimeout(() => {
    if (vm) {
      const markdownSection = document.querySelector('.markdown-section');
      if (markdownSection) {
        const compiler = new window.DocsifyCompiler({
          basePath: '/',
          relativePath: false,
          fallbackLanguages: [],
          nameLink: '/',
          routerMode: 'hash'
        }, vm.router);

        const html = compiler.compile(editor.value);
        markdownSection.innerHTML = html;
        window.Prism.highlightAll();

        // Re-render all mermaid diagrams
        const mermaidDivs = markdownSection.querySelectorAll('pre[data-lang="mermaid"] code');
        mermaidDivs.forEach((div, index) => {
          const code = div.textContent;
          const svgName = `mermaid-svg-${svgCounter++}`;
          const MERMAID_CONTAINER_ID = `${svgName}-container`;

          // Replace the <pre> element with our container
          const container = document.createElement('div');
          container.className = 'mermaid';
          container.id = MERMAID_CONTAINER_ID;
          div.parentElement.replaceWith(container);

          // Render the diagram
          mermaid.render(svgName, code).then(({ svg }) => {
            const containerElement = document.getElementById(MERMAID_CONTAINER_ID);
            if (containerElement) {
              containerElement.innerHTML = svg;
            }
          });
        });
      }
    }
  }, 500);
};

  editor.addEventListener('input', updatePreview);
</script>
