async function deleteDocumentsFromQdrant() {
  try {
    // Initialize OpenAI embeddings
    const embeddings = new OpenAIEmbeddings({
      model: "text-embedding-3-large",
      openAIApiKey: openAIApiKey
    });

    // Connect to existing Qdrant collection
    const vectorStore = await QdrantVectorStore.fromExistingCollection(embeddings, {
      url: url,
      apiKey: apiKey,
      collectionName: collectionName,
    });

    const fileIds = items.json.appended_id.map(item => item);

    console.log(fileIds)

    // Delete points by fileId
    const deletionResults = await Promise.all(fileIds.map(async (file_id) => {
      const filter = {
        must: [
          {
            key: "metadata.file_id",
            match: { value: file_id }
          }
        ]
      };

      try {
        // Access the underlying Qdrant client to perform the deletion
        await vectorStore.client.delete(collectionName, { filter });
        return { file_id, status: "deleted" };
      } catch (error) {
        console.error(`Error deleting documents for fileId ${file_id}:`, error);
        return { file_id, status: "error", message: error.message };
      }
    }));

    return deletionResults;
  } catch (error) {
    console.error("An error occurred during the deletion process:", error);
    return error.message;
  }
}

// Execute the deletion process
try {
  const result = await deleteDocumentsFromQdrant();
  console.log("Deletion process completed:", result);
  return [];
} catch (error) {
  console.error("Failed to execute deletion process:", error);
  return [{ json: { error } }];
}

