 "Êñá‰ª∂Êñá‰ª∂Â§π", "returnAll": true}, "credentials": {"googleDriveOAuth2Api": {"id": "UhdXGYLTAJbsa0xX", "name": "Google Drive account"}}, "typeVersion": 3}, {"id": "3bc4f005-c6c8-4f73-816a-1573d6dfb62f", "name": "ÊñáÊú¨ÂµåÂÖ•3-Â§ßÂûã", "type": "@n8n/n8n-nodes-langchain.embeddingsOpenAi", "position": [480, 720], "parameters": {"model": "ÊñáÊú¨ÂµåÂÖ•-3-Â§ßÂûã", "options": {}}, "credentials": {"openAiApi": {"id": "jEMSvKmtYfzAkhe6", "name": "OpenAi account"}}, "typeVersion": 1}, {"id": "5d42b34a-7156-4163-a2c4-a9cb3c984c96", "name": "Ë∞∑Ê≠åÊñá‰ª∂Â§πID", "type": "n8n-nodes-base.set", "position": [-820, -160], "parameters": {"options": {}, "assignments": {"assignments": [{"id": "e6f6188f-c895-4c8c-b39a-0ef55b490fd6", "name": "folder_id", "type": "string", "value": "[Your-Google-Folder-ID]"}]}}, "typeVersion": 3.4}, {"id": "7be310c2-d30f-42a2-8f57-33c7b184e429", "name": "GPT-4Ëø∑‰Ω†Áâà1", "type": "@n8n/n8n-nodes-langchain.lmChatOpenAi", "position": [-400, 940], "parameters": {"options": {}}, "credentials": {"openAiApi": {"id": "jEMSvKmtYfzAkhe6", "name": "OpenAi account"}}, "typeVersion": 1}, {"id": "18aacfd9-3f6e-4fba-9f68-d8cc95bc667b", "name": "ÊåâÊñá‰ª∂IDÂà†Èô§QdrantÁÇπ", "type": "@n8n/n8n-nodes-langchain.code", "position": [-500, 800], "parameters": {"code": {"execute": {"code": "const { QdrantVectorStore } = require("@langchain/qdrant");
const { OpenAIEmbeddings } = require("@langchain/openai");

// Qdrant connection details
const url = "http://localhost:6333/";
const apiKey = "";

// OpenAI API configuration
const openAIApiKey = "[Your-OpenAI-API-Key]";

// Get input data
const items = this.getInputData()[0];
// console.log(items)

const collectionName =  items.json.qdrant_collection_name;
// console.log(collectionName)

async function deleteDocumentsFromQdrant() {
  try {
    // Initialize OpenAI embeddings
    const embeddings = new OpenAIEmbeddings({
      model: "text-embedding-3-large",
      openAIApiKey: openAIApiKey
    });

    // Connect to existing Qdrant collection
    const vectorStore = await QdrantVectorStore.fromExistingCollection(embeddings, {
      url: url,
      apiKey: apiKey,
      collectionName: collectionName,
    });

    const fileIds = items.json.appended_id.map(item => item);

    console.log(fileIds)

    // Delete points by fileId
    const deletionResults = await Promise.all(fileIds.map(async (file_id) => {
      const filter = {
        must: [
          {
            key: "metadata.file_id",
            match: { value: file_id }
          }
        ]
      };

      try {
        // Access the underlying Qdrant client to perform the deletion
        await vectorStore.client.delete(collectionName, { filter });
        return { file_id, status: "deleted" };
      } catch (error) {
        console.error(`Error deleting documents for fileId ${file_id}:`, error);
        return { file_id, status: "error", message: error.message };
      }
    }));

    return deletionResults;
  } catch (error) {
    console.error("An error occurred during the deletion process:", error);
    return error.message;
  }
}

// Execute the deletion process
try {
  const result = await deleteDocumentsFromQdrant();
  console.log("Deletion process completed:", result);
  return [];
} catch (error) {
  console.error("Failed to execute deletion process:", error);
  return [{ json: { error } }];
}

