{"meta":{"instanceId":"1954c8c806bedb8f0628725b26b786028ade16c78a82bc25deb9dd961e036832"},"nodes":[{"id":"36d0b0d4-b454-4a9b-8168-bcc7942a7cc7","name":"Input Arguments","type":"n8n-nodes-base.set","position":[520,740],"parameters":{"options":{},"assignments":{"assignments":[{"id":"ccabe9f4-7911-4488-a75b-7c5779fb2014","name":"timeZone","type":"string","value":"=America/Chicago"},{"id":"b802d976-78f5-4c00-8764-f8c49eaded29","name":"endtime","type":"string","value":"={{ $json.body.message.toolCalls[0].function.arguments.endtime }}"},{"id":"02d58122-6a0f-4bdb-9914-6f50d2af6df4","name":"starttime","type":"string","value":"={{ $json.body.message.toolCalls[0].function.arguments.starttime }}"},{"id":"c1249493-a1d7-4a91-9468-9e5c49430d2e","name":"body.message.toolCalls[0].id","type":"string","value":"={{ $json.body.message.toolCalls[0].id }}"},{"id":"2d1e0d9a-4c70-488e-b430-b8137fd54970","name":"customer.number","type":"string","value":"={{ $json.body.message.call.customer.number }}"}]}},"typeVersion":3.3},{"id":"2d8485ad-9007-4664-9182-7eda25fc96ee","name":"Format response","type":"n8n-nodes-base.itemLists","position":[2000,840],"parameters":{"include":"allFieldsExcept","options":{},"aggregate":"aggregateAllItemData","operation":"concatenateItems","fieldsToExclude":"sort","destinationFieldName":"response"},"typeVersion":3},{"id":"b23c75e0-3697-4137-a595-cf26fedaa898","name":"Sort","type":"n8n-nodes-base.itemLists","position":[1760,840],"parameters":{"options":{},"operation":"sort","sortFieldsUi":{"sortField":[{"fieldName":"sort"}]}},"typeVersion":3},{"id":"660e3d2f-a424-4e76-8c13-5b62b9f22202","name":"Available Start Times & Ranges","type":"n8n-nodes-base.code","position":[2240,840],"parameters":{"jsCode":"// Input data
const inputData = $input.all()[0].json.response;

// Define workday hours in CST
const WORKDAY_START = "09:00:00 CST";
const WORKDAY_END = "18:00:00 CST";
const SLOT_DURATION = 30 * 60 * 1000; // 30 minutes in milliseconds

// Helper to parse CST datetime strings
const parseCST = (datetime) => {
  const parsedDate = new Date(datetime.replace(" CST", "-06:00"));
  return isNaN(parsedDate) ? null : parsedDate;
};

// Function to generate 30-minute start times
const generateStartTimes = (start, end) => {
  const startTimes = [];
  let current = new Date(start);

  while (current < end) {
    startTimes.push(
      current.toLocaleTimeString('en-US', {
        timeZone: 'CST',
        hour: '2-digit',
        minute: '2-digit',
      })
    );
    current = new Date(current.getTime() + SLOT_DURATION);
  }

  return startTimes;
};

// Function to find wide open ranges
const findWideOpenRanges = (startTimes) => {
  if (startTimes.length < 3) return []; // Not enough slots for a wide open range

  const ranges = [];
  let rangeStart = null;
  let consecutiveCount = 0;

  for (let i = 0; i < startTimes.length - 1; i++) {
    const currentTime = parseCST(`2000-01-01 ${startTimes[i]} CST`);
    const nextTime = parseCST(`2000-01-01 ${startTimes[i + 1]} CST`);
    const diff = nextTime - currentTime;

    if (diff === SLOT_DURATION) {
      consecutiveCount += 1;
      if (rangeStart === null) rangeStart = startTimes[i];
    } else {
      if (consecutiveCount >= 2) {
        ranges.push(`${rangeStart} to ${startTimes[i]}`);
      }
      rangeStart = null;
      consecutiveCount = 0;
    }
  }

  // Handle the final range
  if (consecutiveCount >= 2) {
    ranges.push(`${rangeStart} to ${startTimes[startTimes.length - 1]}`);
  }

  return ranges;
};

// Group meetings by date, ignoring invalid dates
const meetingsByDate = inputData.reduce((acc, meeting) => {
  const start = parseCST(meeting.start);
  const end = parseCST(meeting.end);

  if (!start || !end) {
    return acc; // Ignore invalid dates
  }

  const dateKey = start.toISOString().split('T')[0];

  if (!acc[dateKey]) {
    acc[dateKey] = [];
  }

  acc[dateKey].push({ start, end });
  return acc;
}, {});

// Generate availability
const availability = Object.keys(meetingsByDate)
  .filter((date) => {
    // Exclude Saturdays (6) and Sundays (0)
    const dayOfWeek = new Date(date).getUTCDay();
    return dayOfWeek !== 0 && dayOfWeek !== 6;
  })
  .map((date) => {
    const workdayStart = parseCST(`${date} ${WORKDAY_START}`);
    const workdayEnd = parseCST(`${date} ${WORKDAY_END}`);

    const dayMeetings = meetingsByDate[date].sort((a, b) => a.start - b.start);

    let availableStartTimes = [];
    let lastEnd = workdayStart;

    for (const meeting of dayMeetings) {
      if (meeting.start > lastEnd) {
        availableStartTimes = availableStartTimes.concat(generateStartTimes(lastEnd, meeting.start));
      }
      lastEnd = meeting.end > lastEnd ? meeting.end : lastEnd;
    }

    if (lastEnd < workdayEnd) {
      availableStartTimes = availableStartTimes.concat(generateStartTimes(lastEnd, workdayEnd));
    }

    const wideOpenRanges = findWideOpenRanges(availableStartTimes);

    return {
      date: new Date(date).toLocaleDateString('en-US', {
        weekday: 'long',
        year: 'numeric',
        month: 'long',
        day: 'numeric',
      }),
      availableStartTimes,
      wideOpenRanges,
    };
  });

// Format output as plaintext
const availableTimes = availability
  .map(({ date, availableStartTimes, wideOpenRanges }) => {
    const times = availableStartTimes.map((time) => `- ${time}`).join('\n');
    const ranges = wideOpenRanges.length
      ? `Wide Open Ranges:\n${wideOpenRanges.map((range) => `- ${range}`).join('\n')}`
      : "Wide Open Ranges: None";

    return `### ${date}\nAvailable Start Times:\n${times}\n\n${ranges}`;
  })
  .join('\n\n');

// Set the output
return {
  json: {
    availableTimes,
  },
};
"},"typeVersion":2},{"id":"f3110658-2f90-4b19-9874-7d6c4e108895","name":"Flatten Slots","type":"n8n-nodes-base.code","position":[2460,840],"parameters":{"mode":"runOnceForEachItem","jsCode":"const flattenSlots = (data) => {
  // If data is missing or empty, return an empty array of slots
  if (!data) {
    return { slots: [] };
  }

  // data is an object whose keys are dates
  // each date key has an array of slot objects
  // we just need to flatten them all into one array
  const flattened = Object.values(data).flat();  // merges all arrays from each date key

  // Return a new object with a single 'slots' array
  return { slots: flattened };
};

// Then assign the flattened slots back to $input.item.json.data
$input.item.json.data = flattenSlots($input.item.json.data);
return $input.item;
"},"typeVersion":2},{"id":"5065439e-34e3-4eaf-8226-8ba7393a5cf3","name":"Enrich Date","type":"n8n-nodes-base.code","position":[2680,840],"parameters":{"mode":"runOnceForEachItem","jsCode":"function formatTimeSlot(dateString) {
  // Format options for date/time with America/Chicago timezone
  const options = {
    timeZone: 'America/Chicago',
    weekday: 'long',
    month: 'long',
    day: 'numeric',
    hour: 'numeric',
    minute: 'numeric',
    hour12: true
  };

  // Create a formatter with timezone support
  const dateFormatter = new Intl.DateTimeFormat('en-US', options);

  // Format the date/time string
  return dateFormatter.format(new Date(dateString));
}

// Process each slot and add formatted time strings to the result
const slots = $input.item.json.data.slots;
const formattedSlots = slots.map(slot => formatTimeSlot(slot.start));

// Attach formatted results to the output
$input.item.json.data.slots = formattedSlots;

return $input.item;
"},"typeVersion":2},{"id":"d8ed3a92-697b-4718-b65f-5276c9a9bfaf","name":"Build Response Payload","type":"n8n-nodes-base.set","position":[2900,840],"parameters":{"options":{},"assignments":{"assignments":[{"id":"5cb05b10-e916-459e-84a2-9c314a859a07","name":"results[0].toolCallId","type":"string","value":"={{ $('Input Arguments').item.json.body.message.toolCalls[0].id }}"},{"id":"552246f9-7afd-404e-9fb3-cb38c7447359","name":"results[0].result","type":"string","value":"={{ $json.availableTimes }}"}]}},"typeVersion":3.4},{"id":"a0697944-c5a6-4ca1-9948-8248940841b2","name":"Booking Payload","type":"n8n-nodes-base.set","position":[1980,1400],"parameters":{"options":{"ignoreConversionErrors":true},"assignments":{"assignments":[{"id":"05bbc797-b781-489c-ab70-e234fe17eb62","name":"id","type":"number","value":"={{ $json.id }}"},{"id":"4bb68abf-18c8-4445-b446-21667abd95aa","name":"description","type":"string","value":"={{ $json.description }}"},{"id":"74a98b77-b9fe-40cc-84c8-fc7303c5cfa6","name":"startTime","type":"string","value":"={{ $json.start.dateTime }}"},{"id":"2934d6a7-9e6b-4038-891c-0b05ba18cb21","name":"endTime","type":"string","value":"={{ $json.end.dateTime }}"},{"id":"10f091c8-5e52-40dc-a294-87625be9af99","name":"status","type":"string","value":"={{ $json.status }}"},{"id":"cdc5e1ab-a29b-447f-8343-ff1c1b168717","name":"Timezone","type":"string","value":"={{ $json.end.timeZone }}"},{"id":"f5b6820c-ab4b-496c-9957-f86753243388","name":"attendees","type":"array","value":"={{ $json.attendees }}"},{"id":"b39a06a5-4fbf-4fdf-9d9a-a07dcb37d157","name":"hangoutLink","type":"string","value":"={{ $json.hangoutLink }}"},{"id":"345f49fc-93bc-48b8-9ced-326139a82119","name":"Title","type":"string","value":"={{ $json.summary }}"}]}},"typeVersion":3.4},{"id":"4f7b157c-f657-48fa-8bb5-a1e074b042eb","name":"Success Response","type":"n8n-nodes-base.set","position":[2200,1400],"parameters":{"options":{},"assignments":{"assignments":[{"id":"2c3894da-7bf7-4a35-95c0-d3d9199dd0ad","name":"r