nvert time to CST America / Chicago","type":"n8n-nodes-base.code","position":[1480,1580],"parameters":{"jsCode":"// Get all input items
const items = $input.all();

// Loop through each item
for (const item of items) {
  // Get the values from the current item's JSON data
  const startTimeUTC = item.json.starttime;
  const endTimeUTC = item.json.endtime;
  const targetTimeZone = item.json.timeZone; // e.g., "America/Chicago"

  // Basic validation: ensure the necessary fields exist
  if (!startTimeUTC || !endTimeUTC || !targetTimeZone) {
    console.warn(`Skipping item due to missing time data or timezone. Item JSON: ${JSON.stringify(item.json)}`);
    item.json.conversionError = "Missing starttime, endtime, or timeZone";
    continue; // Move to the next item
  }

  try {
    // --- Start Time Conversion ---
    // Parse the original UTC ISO string using Luxon (NO $ prefix)
    const startDt = luxon.DateTime.fromISO(startTimeUTC, { zone: 'utc' });

    // Convert the DateTime object to the target timezone
    const startDtTargetZone = startDt.setZone(targetTimeZone);

    // Check if the conversion was valid
    if (!startDtTargetZone.isValid) {
      throw new Error(`Failed to convert start time. Reason: ${startDtTargetZone.invalidReason || 'Unknown'}`);
    }

    // Format the result back into an ISO string with the correct offset
    item.json.starttime = startDtTargetZone.toISO();

    // --- End Time Conversion ---
    // Parse the original UTC ISO string using Luxon (NO $ prefix)
    const endDt = luxon.DateTime.fromISO(endTimeUTC, { zone: 'utc' });

    // Convert the DateTime object to the target timezone
    const endDtTargetZone = endDt.setZone(targetTimeZone);

     // Check if the conversion was valid
    if (!endDtTargetZone.isValid) {
      throw new Error(`Failed to convert end time. Reason: ${endDtTargetZone.invalidReason || 'Unknown'}`);
    }

    // Format the result back into an ISO string with the correct offset
    item.json.endtime = endDtTargetZone.toISO();

    // Optionally remove the error flag if conversion was successful this time
    delete item.json.conversionError;

  } catch (error) {
    console.error(`Error converting time for item: ${JSON.stringify(item.json)}. Error: ${error.message}`);
    // Add/update the error flag to the item's JSON
    item.json.conversionError = error.message;
  }
}
// Return the modified array of items
return items;"},"typeVersion":2},{"id":"2c8b2884-14d3-4bd4-92d8-6e402ca3a8de","name":"Create Event","type":"n8n-nodes-base.googleCalendar","onError":"continueErrorOutput","position":[1700,1580],"parameters":{"end":"={{ $json.endtime }}","start":"={{ $json.starttime }}","calendar":{"__rl":true,"mode":"list","value":"pratik@customaistudio.io","cachedResultName":"pratik@customaistudio.io"},"additionalFields":{"allday":"no","summary":"={{ $json.Tittle }}","showMeAs":"opaque","attendees":["={{ $json.email }}"],"description":"={{ $json.notes }}","conferenceDataUi":{"conferenceDataValues":{"conferenceSolution":"hangoutsMeet"}}}},"credentials":{},"typeVersion":1.3},{"id":"b8890ab2-9850-4608-996d-45c8a6d3a52e","name":"Respond to Vapi","type":"n8n-nodes-base.respondToWebhook","onError":"continueRegularOutput","position":[2480,1580],"parameters":{"options":{},"respondWith":"json","responseBody":"={
