"}]}},"credentials":{"openAiApi":{"id":"6h3DfVhNPw9I25nO","name":"OpenAi account"}},"typeVersion":1.8},{"id":"30394110-bc11-4139-8303-f536e22733d4","name":"Split Script","type":"n8n-nodes-base.code","position":[1300,1040],"parameters":{"jsCode":"const input = $input.first().json;

// Auto-detect AI output path
let aiOutput = "";

if (input?.choices?.[0]?.message?.content) {
  aiOutput = input.choices[0].message.content;
} else if (input?.message?.content) {
  aiOutput = input.message.content;
} else if (typeof input?.content === "string") {
  aiOutput = input.content;
} else {
  // Optional: expose the input in case of failure for debug
  throw new Error("❌ No valid AI output found. Check the structure of the input.");
}

const lines = aiOutput.split('\n').map(l => l.trim());
let text1 = "";
let text2 = "";

for (const line of lines) {
  if (line.toLowerCase().startsWith("text1:")) {
    text1 = line.replace(/^text1:\s*/i, '');
  } else if (line.toLowerCase().startsWith("text2:")) {
    text2 = line.replace(/^text2:\s*/i, '');
  }
}

return [
  {
    json: {
      text1,
      text2
    }
  }
];
"},"typeVersion":2},{"id":"15d7845c-0e0b-4f06-b48e-5ea8add6501a","name":"Merge Video + Music","type":"n8n-nodes-base.httpRequest","position":[80,1640],"parameters":{"url":"https://api.json2video.com/v2/movies","method":"POST","options":{},"jsonBody":"={
