{"meta":{"instanceId":"="},"nodes":[{"id":"a2d54127-d1d1-44d2-859e-b89e2e6c3b4d","name":"If","type":"n8n-nodes-base.if","position":[260,260],"parameters":{"options":{},"conditions":{"options":{"version":2,"leftValue":"","caseSensitive":true,"typeValidation":"strict"},"combinator":"and","conditions":[{"id":"=","operator":{"type":"string","operation":"contains"},"leftValue":"={{ $json.subject }}","rightValue":"CSRD Reporting"}]}},"typeVersion":2.2},{"id":"6a664023-ea8c-4973-b3ac-13a9e0664a58","name":"Check the format","type":"n8n-nodes-base.code","position":[960,260],"parameters":{"jsCode":"const content = $input.first().json.xhtml_content;

// Helper to extract tags
function extractTags(tagName) {
  const regex = new RegExp(`<${tagName}[^>]*>(.*?)<\\/${tagName}>`, 'gs');
  let matches = [];
  let match;
  while ((match = regex.exec(content)) !== null) {
    matches.push(match[1].trim());
  }
  return matches;
}

// Basic Tests
const headerPresent = /<ix:header>/i.test(content);
const governanceTag = /<ix:nonNumeric[^>]*name="esrs:SustainabilityGovernance"/i.test(content);
const strategyTag = /<ix:nonNumeric[^>]*name="esrs:StrategySustainability"/i.test(content);

// KPI Tags
const kpiTags = ["esrs:GHGScope1Emissions", "esrs:GHGScope2Emissions", "esrs:GHGScope3Emissions"];
const kpiMatches = kpiTags.filter(tag => content.includes(tag));

// Check for empty tags
const emptyNonNumeric = (content.match(/<ix:nonNumeric[^>]*>\s*<\/ix:nonNumeric>/g) || []).length;

// Check duplicate text
const nonNumericValues = extractTags("ix:nonNumeric");
const duplicates = [...new Set(nonNumericValues.filter((v, i, arr) => arr.indexOf(v) !== i))];

// Final Result
return [
  {
    json: {
      audit_results:{
      total_nonNumeric_tags: nonNumericValues.length,
      total_kpis_found: kpiMatches.length,
      empty_disclosures: emptyNonNumeric,
      governance_check: governanceTag ? "PASS" : "MISSING",
      strategy_check: strategyTag ? "PASS" : "MISSING",
      header_check: headerPresent ? "PASS" : "MISSING",
      duplicate_disclosures: duplicates,
      }

    }
  }
];
