 full execution JSON
const exec = items[0].json;

// 2) Build execution‐level metadata
const meta = {
  executionId:    exec.id,
  finished:       exec.finished,
  mode:           exec.mode,
  status:         exec.status,
  createdAt:      exec.createdAt,
  startedAt:      exec.startedAt,
  stoppedAt:      exec.stoppedAt,
  deletedAt:      exec.deletedAt,
  workflowId:     exec.workflowId,
  workflowName:   exec.workflowData?.name,
  retryOf:        exec.retryOf,
  retrySuccessId: exec.retrySuccessId,
};

// 3) Identify trigger node name from startData
const runNodeFilter = exec.data?.startData?.runNodeFilter || [];
const triggerNodeName = runNodeFilter[0] || null;

// 4) Grab the raw trigger runData
const runData = exec.data?.resultData?.runData || {};
const triggerRuns = triggerNodeName ? (runData[triggerNodeName] || []) : [];

// 5) Extract the JSON payload from the first run of the trigger
let triggerPayload = {};
if (triggerRuns.length && triggerRuns[0].data?.main?.[0]?.[0]?.json) {
  triggerPayload = triggerRuns[0].data.main[0][0].json;
}

// 6) Merge trigger info into meta
meta.triggerNodeName = triggerNodeName;
meta.triggerPayload  = triggerPayload;

// 7) Now scan for all node errors, **excluding** any nodeName that contains “SERP”
const allErrors = [];
for (const [nodeName, runs] of Object.entries(runData)) {
  // Skip any of the SERP nodes
  if (nodeName.includes('SERP')) continue;

  runs.forEach(run => {
    if (run.executionStatus === 'error') {
      const err     = run.error || exec.data.resultData.error || {};
      const nodeDef = err.node || run.node || {};

      allErrors.push({
        ...meta,                    // exec + trigger metadata

        nodeName,
        nodeId:        nodeDef.id,
        nodeType:      nodeDef.type,
        nodeLabel:     nodeDef.name,

        startTime:     run.startTime,
        executionTime: run.executionTime,
        source:        run.source,

        errorName:        err.name,
        errorMessage:     err.message,
        errorDescription: err.description,
        httpCode:         err.httpCode,
        messages:         err.messages,
        context:          err.context,
        stack:            err.stack,

        parameters:       nodeDef.parameters,
        credentials:      nodeDef.credentials,
      });
    }
  });
}

// 8) Return results
if (!allErrors.length) {
  return [{ json: { message: '✅ No (non‑SERP) errors found in this execution.' } }];
}
return allErrors.map(e => ({ json: e }));
"},"typeVersion":2},{"id":"a26fb0c8-99eb-466d-b201-89c402fa1af4","name":"Error Solver Agent","type":"@n8n/n8n-nodes-langchain.agent","position":[500,-40],"parameters":{"text":"=Can you please help me with this error that occured in my n8n workflow? {{ JSON.stringify($json) }}","options":{"systemMessage":"You are an seasoned n8n expert with specializations in managing n8n instances and workflows. The user will provide a detailed error json object and your goal is to review, analyze and understand the error and using your expertise diagnose the error and provide a detailed report to the user with your diagnosis, cause and resolution so the user understands and can immediately fix the issue."},"promptType":"define","hasOutputParser":true},"typeVersion":1.8},{"id":"8cfd7229-3ff1-4ba1-a67d-caa21be8064f","name":"Set Diagnosis Fields","type":"n8n-nodes-base.set","position":[876,-40],"parameters":{"options":{},"assignments":{"assignments":[{"id":"fac5fbee-d63d-4148-b047-5ed5af4f2574","name":"error.diagnosis","type":"string","value":"={{ $json.output.diagnosis }}"},{"id":"ece9388d-f667-4984-a143-7241f622fe76","name":"error.cause","type":"string","value":"={{ $json.output.cause }}"},{"id":"acb6b34a-a651-42fc-a44a-331b2e0d745c","name":"error.resolution","type":"string","value":"={{ $json.output.resolution }}"},{"id":"c765754b-d6d5-4592-ac3f-99a350bc3c19","name":"error.workflowName","type":"string","value":"={{ $('Extract Error Details').item.json.workflowName }}"},{"id":"dabebc62-3e0c-4d22-afbf-54ba66a912fb","name":"error.workflowId","type":"string","value":"={{ $('Extract Error Details').item.json.workflowId }}"},{"id":"6ab19800-9a0f-439f-bf62-7a7afc5bf958","name":"workflowLink","type":"string","value":"={{ $execution.resumeUrl.split('/').slice(0, 3).join('/') }}/workflow/{{ $('Extract Error Details').item.json.workflowId }}"},{"id":"29daaea5-052b-46d4-8192-141db159bff2","name":"error.executionId","type":"string","value":"={{ $('Extract Error Details').item.json.executionId }