omer }}"},{"name":"discount_codes","value":"={{ $('Filter').item.json.discount_codes}}"},{"name":"shippingcost","value":"={{ $('Filter').item.json.total_shipping_price_set.shop_money.amount }}"},{"name":"line_items","value":"={{ $('Filter').item.json.line_items }}"},{"name":"fulfillment_orders","value":"={{ $json.fulfillment_orders }}"},{"name":"currency","value":"={{ $('Filter').item.json.currency }}"},{"name":"=created_at","value":"={{ $('Filter').item.json.created_at }}"},{"name":"gateway","value":"={{ $('Filter').item.json.payment_gateway_names[0] }}"},{"name":"total_tax","value":"={{ $('Filter').item.json.total_tax }}"},{"name":"total_discounts","value":"={{ $('Filter').item.json.total_discounts*-1 }}"}]},"options":{},"keepOnlySet":true},"typeVersion":2},{"id":"d5120009-2efe-4ef1-9450-eedd475f95c7","name":"orderPreprocessing","type":"n8n-nodes-base.code","position":[1340,300],"parameters":{"mode":"runOnceForEachItem","jsCode":"const orderJson = $input.item.json;

// Create a map of line_item_id to assigned_location_id
const lineItemToLocationMap = {};
orderJson.fulfillment_orders.forEach(fulfillmentOrder => {
  fulfillmentOrder.line_items.forEach(lineItem => {
    lineItemToLocationMap[lineItem.line_item_id] = fulfillmentOrder.assigned_location_id;
  });
});

// Update the line_items array with assigned_location_id
orderJson.line_items.forEach(lineItem => {
  const assignedLocationId = lineItemToLocationMap[lineItem.id];
  if (assignedLocationId !== undefined) {
    lineItem.assigned_location_id = assignedLocationId;
  }
});

// Add a new property 'pairedItem' to orderJson with the value of $itemIndex
orderJson.pairedItem = $itemIndex;

// Add a new line item with specified fields for taxes if taxesAsLineItem is true
if ($('D365 BC Environment Settings').item.json.taxesAsLineItem) {
  const newLineItem = {
    "sku": $('D365 BC Environment Settings').item.json.taxItemSku,
    "price": orderJson.total_tax,
    "quantity": 1
  };
  orderJson.line_items.push(newLineItem);
}

// Add a new line item with specified fields for discount
if ($('D365 BC Environment Settings').item.json.discountsAsLineItem) {
const newDiscountLineItem = {
  "sku": $('D365 BC Environment Settings').item.json.discountItemSku,
  "price": orderJson.total_discounts,
  "quantity": 1
};
orderJson.line_items.push(newDiscountLineItem);
}

// Return the modified orderJson
return orderJson;
